mapper.select:
SELECT
o.id, o.tenant_id, o.channel_id, o.template_code, o.template_type, o.template_name, r.rel_status template_status, o.created_user_id, o.created_time, o.updated_user_id, o.updated_time, o.is_deleted
FROM
t_zero_message_template o left join t_zero_rel_user_message_template r on o.template_id=r.template_id
WHERE
o.is_deleted = 0

    and r.user_id=#{userId}


    and o.channel_id=#{channelId}

and o.tenant_id=#{tenantId}
limit #{currentPage}, #{pageSize}

SELECT
count(o.id)
FROM
t_zero_message_template o left join t_zero_rel_user_message_template r on o.template_id=r.template_id
WHERE
o.is_deleted = 0

    and r.user_id=#{userId}


    and o.channel_id=#{channelId}

and o.tenant_id=#{tenantId}
